function getElementByID(q, id) {
  for (var i = 0; i < q.length; i++) {
    if (q[i].id == id) {
      return q[i];
    }
  }
}

function filterQueue(q, on) {
  var result = [];

  for (var i = 0; i < q.length; i++) {
    if (q[i].id != on) {
      result.push(q[i]);
    }
  }

  return result;
}

function clearQueues() {
  nextQueue = [];
  timeoutQueue = [];
  violationQueue = [];
}

function addToNextQueue(m) {
  nextQueue = filterQueue(nextQueue, m.id);
  timeoutQueue = filterQueue(timeoutQueue, m.id);
  violationQueue = filterQueue(violationQueue, m.id);
  nextQueue.push(m);
  updateDisplay();
}

function addToTimeoutQueue(m) {
  nextQueue = filterQueue(nextQueue, m.id);
  timeoutQueue = filterQueue(timeoutQueue, m.id);
  violationQueue = filterQueue(violationQueue, m.id);
  timeoutQueue.push(m);
  updateDisplay();
}

function addToViolationQueue(m) {
  nextQueue = filterQueue(nextQueue, m.id);
